#!/bin/bashecho "===> Memperbarui paket..."sudo apt updateecho "===> Menginstall dependensi..."sudo apt install python3 python3-pip git unzip -yecho "===> Menghapus folder lama (jika ada)..."rm -rf PyMacroRecordecho "===> Download PyMacroRecord dari GitHub..."git clone https://github.com/mousemacros/PyMacroRecord.gitcd PyMacroRecord || { echo "Folder tidak ditemukan!"; exit 1; }echo "===> Menginstall library Python..."if [ -f requirements.txt ]; then    pip3 install -r requirements.txtelse    pip3 install keyboard mouse pyautoguifiecho "===> Menjalankan PyMacroRecord..."cd srcpython3 main.pyecho "===> Selesai!"